import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { filmsAPI } from '../services/api';

function FilmDetails() {
  const { filmId } = useParams();
  const navigate = useNavigate();
  const [film, setFilm] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [customerId, setCustomerId] = useState('');
  const [renting, setRenting] = useState(false);
  const [rentSuccess, setRentSuccess] = useState(false);

  useEffect(() => {
    loadFilm();
  }, [filmId]);

  const loadFilm = async () => {
    try {
      setLoading(true);
      const data = await filmsAPI.getFilmById(filmId);
      setFilm(data);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleRent = async (e) => {
    e.preventDefault();
    if (!customerId || isNaN(customerId)) {
      alert('Please enter a valid customer ID');
      return;
    }

    try {
      setRenting(true);
      await filmsAPI.rentFilm(filmId, parseInt(customerId));
      setRentSuccess(true);
      setCustomerId('');
      setTimeout(() => setRentSuccess(false), 3000);
    } catch (err) {
      alert(`Error renting film: ${err.message}`);
    } finally {
      setRenting(false);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">Error: {error}</div>;
  if (!film) return <div>Film not found</div>;

  return (
    <div>
      <button onClick={() => navigate(-1)} className="btn btn-secondary" style={{ marginBottom: '1rem' }}>
        ‚Üê Back
      </button>

      <h1 className="page-title">{film.title}</h1>

      <div className="card">
        <p><strong>Film ID:</strong> {film.film_id}</p>
        <p><strong>Category:</strong> {film.category}</p>
        {film.description && <p><strong>Description:</strong> {film.description}</p>}
        {film.release_year && <p><strong>Release Year:</strong> {film.release_year}</p>}
        {film.length && <p><strong>Length:</strong> {film.length} minutes</p>}
        {film.rating && <p><strong>Rating:</strong> {film.rating}</p>}
        {film.rental_rate && <p><strong>Rental Rate:</strong> ${film.rental_rate}</p>}
      </div>

      {/* Rent Film Section */}
      <div className="card" style={{ marginTop: '2rem' }}>
        <h2 className="section-title">Rent This Film</h2>
        {rentSuccess && (
          <div style={{ backgroundColor: '#d4edda', color: '#155724', padding: '1rem', borderRadius: '4px', marginBottom: '1rem' }}>
            Film rented successfully!
          </div>
        )}
        <form onSubmit={handleRent}>
          <div className="form-group">
            <label>Customer ID:</label>
            <input
              type="number"
              className="form-control"
              value={customerId}
              onChange={(e) => setCustomerId(e.target.value)}
              required
              placeholder="Enter customer ID"
            />
          </div>
          <button type="submit" className="btn btn-success" disabled={renting}>
            {renting ? 'Processing...' : 'Rent Film'}
          </button>
        </form>
      </div>
    </div>
  );
}

export default FilmDetails;
