import React, { useState, useEffect } from 'react';
import { customersAPI } from '../services/api';

function CustomerPage() {
  // set constants
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  // load data
  useEffect(() => {
    if (!isSearching) {
      loadCustomers();
    }
  }, [currentPage, isSearching]);

  const loadCustomers = async () => {
    setLoading(true);
    const data = await customersAPI.getCustomers(currentPage, 21);
    setCustomers(data.customers || data);
    setTotalPages(data.totalPages || 1);
    setError(null);
    setLoading(false);
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      setIsSearching(false);
      setCurrentPage(1);
      return;
    }
    setLoading(true);
    setIsSearching(true);
    const results = await customersAPI.searchCustomers(searchQuery);
    setCustomers(results);
    setError(null);
    setLoading(false);
  };



  return (
    <div>
      <h1 className="page-title">Customers</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          className="search-bar"
          placeholder="Search by customer ID, first name, or last name..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <button type="submit" className="btn btn-search">Search</button>
          {isSearching && (
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={() => {
                setSearchQuery('');
                setIsSearching(false);
                setCurrentPage(1);
              }}
            >
              Clear Search
            </button>
          )}
        </div>
      </form>



      {error && <div className="error">Error: {error}</div>}
      {loading && <div className="loading">Loading...</div>}

      {/* Customer List */}
      {!loading && customers.length === 0 && (
        <p>No customers found.</p>
      )}

      {!loading && customers.length > 0 && (
        <div className="grid grid-2">
          {customers.map(customer => (
            <div key={customer.customer_id} className="card">
              <h3>{customer.first_name} {customer.last_name}</h3>
              <p><strong>ID:</strong> {customer.customer_id}</p>
              <p><strong>Email:</strong> {customer.email}</p>
            </div>
          ))}
        </div>
      )}

      {/* Pagination */}
      {!isSearching && totalPages > 1 && (
        <div className="pagination">
          <button
            onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
            disabled={currentPage === 1}
            className="btn"
          >
            Previous
          </button>
          <span>Page {currentPage} of {totalPages}</span>
          <button
            onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
            disabled={currentPage === totalPages}
            className="btn"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}

export default CustomerPage;
