import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { filmsAPI } from '../services/api';

function FilmsPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [films, setFilms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [hasSearched, setHasSearched] = useState(false);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    try {
      setLoading(true);
      setError(null);
      const results = await filmsAPI.searchFilms(searchQuery);
      setFilms(results);
      setHasSearched(true);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1 className="page-title">Films</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch}>
        <input
          type="text"
          className="search-bar"
          placeholder="Search by film name, actor name, or genre..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button type="submit" className="btn btn-primary">Search</button>
      </form>

      {error && <div className="error">Error: {error}</div>}
      {loading && <div className="loading">Searching...</div>}

      {/* Search Results */}
      {hasSearched && !loading && (
        <div>
          <h2 className="section-title">Search Results ({films.length})</h2>
          {films.length === 0 ? (
            <p>No films found. Try a different search term.</p>
          ) : (
            <div className="grid grid-2">
              {films.map(film => (
                <div key={film.film_id} className="card">
                  <h3>{film.title}</h3>
                  <p><strong>Category:</strong> {film.category}</p>
                  {film.description && <p>{film.description.substring(0, 100)}...</p>}
                  <div style={{ marginTop: '1rem' }}>
                    <Link to={`/films/${film.film_id}`} className="btn btn-primary">
                      View Details
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default FilmsPage;
