import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { customersAPI } from '../services/api';

function CustomerDetails() {
  const { customerId } = useParams();
  const navigate = useNavigate();
  const [customer, setCustomer] = useState(null);
  const [rentals, setRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    loadData();
  }, [customerId]);

  const loadData = async () => {
    try {
      setLoading(true);
      const [customerData, rentalsData] = await Promise.all([
        customersAPI.getCustomerById(customerId),
        customersAPI.getCustomerRentals(customerId)
      ]);
      setCustomer(customerData);
      setRentals(rentalsData);
      setFormData(customerData);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await customersAPI.updateCustomer(customerId, formData);
      setIsEditing(false);
      loadData();
    } catch (err) {
      alert(`Error updating customer: ${err.message}`);
    }
  };

  const handleReturn = async (rentalId) => {
    if (!window.confirm('Mark this rental as returned?')) return;

    try {
      await customersAPI.returnRental(rentalId);
      loadData();
    } catch (err) {
      alert(`Error returning rental: ${err.message}`);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">Error: {error}</div>;
  if (!customer) return <div>Customer not found</div>;

  return (
    <div>
      <button onClick={() => navigate(-1)} className="btn btn-secondary" style={{ marginBottom: '1rem' }}>
        ‚Üê Back
      </button>

      <h1 className="page-title">Customer Details</h1>

      {/* Customer Info */}
      <div className="card">
        {!isEditing ? (
          <>
            <h2>{customer.first_name} {customer.last_name}</h2>
            <p><strong>Customer ID:</strong> {customer.customer_id}</p>
            <p><strong>Email:</strong> {customer.email}</p>
            {customer.address && <p><strong>Address:</strong> {customer.address}</p>}
            {customer.city && <p><strong>City:</strong> {customer.city}</p>}
            {customer.country && <p><strong>Country:</strong> {customer.country}</p>}
            {customer.phone && <p><strong>Phone:</strong> {customer.phone}</p>}
            <button 
              className="btn btn-primary" 
              onClick={() => setIsEditing(true)}
              style={{ marginTop: '1rem' }}
            >
              Edit Customer
            </button>
          </>
        ) : (
          <form onSubmit={handleUpdate}>
            <div className="form-group">
              <label>First Name *</label>
              <input
                type="text"
                className="form-control"
                value={formData.first_name || ''}
                onChange={(e) => setFormData({...formData, first_name: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Last Name *</label>
              <input
                type="text"
                className="form-control"
                value={formData.last_name || ''}
                onChange={(e) => setFormData({...formData, last_name: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Email *</label>
              <input
                type="email"
                className="form-control"
                value={formData.email || ''}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                className="form-control"
                value={formData.address || ''}
                onChange={(e) => setFormData({...formData, address: e.target.value})}
              />
            </div>
            <div className="form-group">
              <label>City</label>
              <input
                type="text"
                className="form-control"
                value={formData.city || ''}
                onChange={(e) => setFormData({...formData, city: e.target.value})}
              />
            </div>
            <div className="form-group">
              <label>Country</label>
              <input
                type="text"
                className="form-control"
                value={formData.country || ''}
                onChange={(e) => setFormData({...formData, country: e.target.value})}
              />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input
                type="tel"
                className="form-control"
                value={formData.phone || ''}
                onChange={(e) => setFormData({...formData, phone: e.target.value})}
              />
            </div>
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <button type="submit" className="btn btn-success">Save Changes</button>
              <button 
                type="button" 
                className="btn btn-secondary"
                onClick={() => {
                  setIsEditing(false);
                  setFormData(customer);
                }}
              >
                Cancel
              </button>
            </div>
          </form>
        )}
      </div>

      {/* Rental History */}
      <h2 className="section-title">Rental History</h2>
      {rentals.length === 0 ? (
        <p>No rental history available.</p>
      ) : (
        <div>
          <table style={{ width: '100%', borderCollapse: 'collapse' }}>
            <thead>
              <tr style={{ backgroundColor: '#f8f9fa', borderBottom: '2px solid #dee2e6' }}>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Rental ID</th>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Film</th>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Rental Date</th>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Return Date</th>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Status</th>
                <th style={{ padding: '0.75rem', textAlign: 'left' }}>Action</th>
              </tr>
            </thead>
            <tbody>
              {rentals.map(rental => (
                <tr key={rental.rental_id} style={{ borderBottom: '1px solid #dee2e6' }}>
                  <td style={{ padding: '0.75rem' }}>{rental.rental_id}</td>
                  <td style={{ padding: '0.75rem' }}>{rental.film_title || rental.title || 'N/A'}</td>
                  <td style={{ padding: '0.75rem' }}>
                    {new Date(rental.rental_date).toLocaleDateString()}
                  </td>
                  <td style={{ padding: '0.75rem' }}>
                    {rental.return_date ? new Date(rental.return_date).toLocaleDateString() : 'Not returned'}
                  </td>
                  <td style={{ padding: '0.75rem' }}>
                    <span style={{
                      padding: '0.25rem 0.5rem',
                      borderRadius: '4px',
                      backgroundColor: rental.return_date ? '#d4edda' : '#fff3cd',
                      color: rental.return_date ? '#155724' : '#856404'
                    }}>
                      {rental.return_date ? 'Returned' : 'Active'}
                    </span>
                  </td>
                  <td style={{ padding: '0.75rem' }}>
                    {!rental.return_date && (
                      <button 
                        className="btn btn-success"
                        onClick={() => handleReturn(rental.rental_id)}
                        style={{ padding: '0.25rem 0.5rem', fontSize: '0.875rem' }}
                      >
                        Mark Returned
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default CustomerDetails;
