import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { filmsAPI } from '../services/api';

function FilmsPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [films, setFilms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isSearching, setIsSearching] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    if (!isSearching) {
      loadFilms();
    }
  }, [currentPage, isSearching]);

  const loadFilms = async () => {
    try {
      setLoading(true);
      const data = await filmsAPI.getAllFilms(currentPage, 20);
      setFilms(data.films || data);
      setTotalPages(data.totalPages || 1);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      setIsSearching(false);
      setCurrentPage(1);
      return;
    }

    try {
      setLoading(true);
      setIsSearching(true);
      const results = await filmsAPI.searchFilms(searchQuery);
      setFilms(results);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1 className="page-title">Films</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          className="search-bar"
          placeholder="Search by film name, actor name, or genre..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <button type="submit" className="btn btn-primary">Search</button>
          {isSearching && (
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={() => {
                setSearchQuery('');
                setIsSearching(false);
                setCurrentPage(1);
              }}
            >
              Clear Search
            </button>
          )}
        </div>
      </form>

      {error && <div className="error">Error: {error}</div>}
      {loading && <div className="loading">Loading...</div>}

      {/* Film List */}
      {!loading && films.length === 0 && (
        <p>No films found.</p>
      )}

      {!loading && films.length > 0 && (
        <>
          {isSearching && <h2 className="section-title">Search Results ({films.length})</h2>}
          <div className="grid grid-2">
            {films.map(film => (
              <div key={film.film_id} className="card">
                <h3>{film.title}</h3>
                <p><strong>Category:</strong> {film.category}</p>
                {film.description && <p>{film.description.substring(0, 100)}...</p>}
                <div style={{ marginTop: '1rem' }}>
                  <Link to={`/films/${film.film_id}`} className="btn btn-primary">
                    View Details
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </>
      )}

      {/* Pagination */}
      {!isSearching && totalPages > 1 && !loading && (
        <div className="pagination">
          <button
            onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
            disabled={currentPage === 1}
            className="btn"
          >
            Previous
          </button>
          <span>Page {currentPage} of {totalPages}</span>
          <button
            onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
            disabled={currentPage === totalPages}
            className="btn"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}

export default FilmsPage;
