import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { filmsAPI } from '../services/api';

function FilmsPage() {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchType, setSearchType] = useState('filmName'); // 'filmName', 'category', 'actor'
  const [films, setFilms] = useState([]);
  const [allSearchResults, setAllSearchResults] = useState([]); // Store all search results for client-side pagination
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isSearching, setIsSearching] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const ITEMS_PER_PAGE = 21;

  useEffect(() => {
    if (!isSearching) {
      loadFilms();
    }
  }, [currentPage, isSearching]);

  const loadFilms = async () => {
    try {
      setLoading(true);
      const data = await filmsAPI.getAllFilms(currentPage, 21);
      setFilms(data.films || data);
      setTotalPages(data.totalPages || 1);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      setIsSearching(false);
      setCurrentPage(1);
      return;
    }

    try {
      setLoading(true);
      setIsSearching(true);
      setCurrentPage(1);
      
      // Build search query based on type
      let searchParam = searchQuery.trim();
      if (searchType === 'category') {
        // For category, pass exact match
        searchParam = searchQuery.trim();
      } else if (searchType === 'actor') {
        // For actor, pass full name
        searchParam = searchQuery.trim();
      }
      
      const results = await filmsAPI.searchFilms(searchParam);
      setAllSearchResults(results);
      
      // Paginate results client-side
      const totalPgs = Math.ceil(results.length / ITEMS_PER_PAGE);
      setTotalPages(totalPgs);
      setFilms(results.slice(0, ITEMS_PER_PAGE));
      
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Handle pagination for search results
  useEffect(() => {
    if (isSearching && allSearchResults.length > 0) {
      const startIdx = (currentPage - 1) * ITEMS_PER_PAGE;
      const endIdx = startIdx + ITEMS_PER_PAGE;
      setFilms(allSearchResults.slice(startIdx, endIdx));
    }
  }, [currentPage, isSearching, allSearchResults]);

  return (
    <div>
      <h1 className="page-title">Films</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={{ marginBottom: '1rem' }}>
        <div style={{ display: 'flex', gap: '0.5rem', marginBottom: '0.5rem', alignItems: 'center' }}>
          <label style={{ fontWeight: 'bold' }}>Search by:</label>
          <select 
            value={searchType} 
            onChange={(e) => setSearchType(e.target.value)}
            style={{ padding: '0.5rem', borderRadius: '4px', border: '1px solid #ccc' }}
          >
            <option value="filmName">Film Name</option>
            <option value="category">Category</option>
            <option value="actor">Actor Name</option>
          </select>
        </div>
        <input
          type="text"
          className="search-bar"
          placeholder={
            searchType === 'filmName' ? 'Enter film name...' :
            searchType === 'category' ? 'Enter category (e.g., Action, Comedy, Drama)...' :
            'Enter actor full name (e.g., John Smith)...'
          }
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <button type="submit" className="btn btn-primary">Search</button>
          {isSearching && (
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={() => {
                setSearchQuery('');
                setIsSearching(false);
                setAllSearchResults([]);
                setCurrentPage(1);
              }}
            >
              Clear Search
            </button>
          )}
        </div>
      </form>

      {error && <div className="error">Error: {error}</div>}
      {loading && <div className="loading">Loading...</div>}

      {/* Film List */}
      {!loading && films.length === 0 && (
        <p>No films found.</p>
      )}

      {!loading && films.length > 0 && (
        <>
          {isSearching && <h2 className="section-title">Search Results ({allSearchResults.length})</h2>}
          <div className="grid grid-2">
            {films.map(film => (
              <div key={film.film_id} className="card">
                <h3>{film.title}</h3>
                <p><strong>Category:</strong> {film.category}</p>
                {film.description && <p>{film.description.substring(0, 100)}...</p>}
                <div style={{ marginTop: '1rem' }}>
                  <Link to={`/films/${film.film_id}`} className="btn btn-primary">
                    View Details
                  </Link>
                </div>
              </div>
            ))}
          </div>
        </>
      )}

      {/* Pagination */}
      {totalPages > 1 && !loading && (
        <div className="pagination">
          <button
            onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
            disabled={currentPage === 1}
            className="btn"
          >
            Previous
          </button>
          <span>Page {currentPage} of {totalPages}</span>
          <button
            onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
            disabled={currentPage === totalPages}
            className="btn"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}

export default FilmsPage;
