// Update this URL to match your Flask backend
const API_BASE_URL = 'http://localhost:5000/api';

// Helper function to handle API responses
const handleResponse = async (response) => {
  if (!response.ok) {
    const error = await response.json().catch(() => ({ message: 'An error occurred' }));
    throw new Error(error.message || 'Request failed');
  }
  return response.json();
};

// Films API
export const filmsAPI = {
  // Get top 5 rented films
  getTopRented: async () => {
    const response = await fetch(`${API_BASE_URL}/films/top-rented`);
    return handleResponse(response);
  },

  // Get film details by ID
  getFilmById: async (filmId) => {
    const response = await fetch(`${API_BASE_URL}/films/${filmId}`);
    return handleResponse(response);
  },

  // Search films by name, actor, or genre
  searchFilms: async (query) => {
    const response = await fetch(`${API_BASE_URL}/films/search?q=${encodeURIComponent(query)}`);
    return handleResponse(response);
  },

  // Rent a film
  rentFilm: async (filmId, customerId, storeId = 1) => {
    const response = await fetch(`${API_BASE_URL}/films/${filmId}/rent`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ customerId, storeId })
    });
    return handleResponse(response);
  }
};

// Actors API
export const actorsAPI = {
  // Get top 5 actors
  getTopActors: async () => {
    const response = await fetch(`${API_BASE_URL}/actors/top`);
    return handleResponse(response);
  },

  // Get actor details by ID
  getActorById: async (actorId) => {
    const response = await fetch(`${API_BASE_URL}/actors/${actorId}`);
    return handleResponse(response);
  },

  // Get top 5 rented films by actor
  getActorTopFilms: async (actorId) => {
    const response = await fetch(`${API_BASE_URL}/actors/${actorId}/top-films`);
    return handleResponse(response);
  }
};

// Customers API
export const customersAPI = {
  // Get all customers with pagination
  getCustomers: async (page = 1, limit = 20) => {
    const response = await fetch(`${API_BASE_URL}/customers?page=${page}&limit=${limit}`);
    return handleResponse(response);
  },

  // Search customers
  searchCustomers: async (query) => {
    const response = await fetch(`${API_BASE_URL}/customers/search?q=${encodeURIComponent(query)}`);
    return handleResponse(response);
  },

  // Get customer by ID
  getCustomerById: async (customerId) => {
    const response = await fetch(`${API_BASE_URL}/customers/${customerId}`);
    return handleResponse(response);
  },

  // Get customer rental history
  getCustomerRentals: async (customerId) => {
    const response = await fetch(`${API_BASE_URL}/customers/${customerId}/rentals`);
    return handleResponse(response);
  },

  // Add new customer
  addCustomer: async (customerData) => {
    const response = await fetch(`${API_BASE_URL}/customers`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(customerData)
    });
    return handleResponse(response);
  },

  // Update customer
  updateCustomer: async (customerId, customerData) => {
    const response = await fetch(`${API_BASE_URL}/customers/${customerId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(customerData)
    });
    return handleResponse(response);
  },

  // Delete customer
  deleteCustomer: async (customerId) => {
    const response = await fetch(`${API_BASE_URL}/customers/${customerId}`, {
      method: 'DELETE'
    });
    return handleResponse(response);
  },

  // Mark rental as returned
  returnRental: async (rentalId) => {
    const response = await fetch(`${API_BASE_URL}/rentals/${rentalId}/return`, {
      method: 'PUT'
    });
    return handleResponse(response);
  }
};

export default {
  filmsAPI,
  actorsAPI,
  customersAPI
};
