import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { customersAPI } from '../services/api';

function CustomerPage() {
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [isSearching, setIsSearching] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [showAddForm, setShowAddForm] = useState(false);
  
  // Form state
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    address: '',
    city: '',
    country: '',
    phone: ''
  });

  useEffect(() => {
    if (!isSearching) {
      loadCustomers();
    }
  }, [currentPage, isSearching]);

  const loadCustomers = async () => {
    try {
      setLoading(true);
      const data = await customersAPI.getCustomers(currentPage, 20);
      setCustomers(data.customers || data);
      setTotalPages(data.totalPages || 1);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) {
      setIsSearching(false);
      setCurrentPage(1);
      return;
    }

    try {
      setLoading(true);
      setIsSearching(true);
      const results = await customersAPI.searchCustomers(searchQuery);
      setCustomers(results);
      setError(null);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleAddCustomer = async (e) => {
    e.preventDefault();
    try {
      await customersAPI.addCustomer(formData);
      setShowAddForm(false);
      setFormData({
        first_name: '',
        last_name: '',
        email: '',
        address: '',
        city: '',
        country: '',
        phone: ''
      });
      loadCustomers();
    } catch (err) {
      alert(`Error adding customer: ${err.message}`);
    }
  };

  const handleDelete = async (customerId) => {
    if (!window.confirm('Are you sure you want to delete this customer?')) return;

    try {
      await customersAPI.deleteCustomer(customerId);
      loadCustomers();
    } catch (err) {
      alert(`Error deleting customer: ${err.message}`);
    }
  };

  return (
    <div>
      <h1 className="page-title">Customers</h1>

      {/* Search Bar */}
      <form onSubmit={handleSearch} style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          className="search-bar"
          placeholder="Search by customer ID, first name, or last name..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <div style={{ display: 'flex', gap: '0.5rem' }}>
          <button type="submit" className="btn btn-primary">Search</button>
          {isSearching && (
            <button 
              type="button" 
              className="btn btn-secondary"
              onClick={() => {
                setSearchQuery('');
                setIsSearching(false);
                setCurrentPage(1);
              }}
            >
              Clear Search
            </button>
          )}
        </div>
      </form>

      {/* Add Customer Button */}
      <button 
        className="btn btn-success" 
        onClick={() => setShowAddForm(!showAddForm)}
        style={{ marginBottom: '1rem' }}
      >
        {showAddForm ? 'Cancel' : '+ Add New Customer'}
      </button>

      {/* Add Customer Form */}
      {showAddForm && (
        <div className="card" style={{ marginBottom: '2rem' }}>
          <h2 className="section-title">Add New Customer</h2>
          <form onSubmit={handleAddCustomer}>
            <div className="form-group">
              <label>First Name *</label>
              <input
                type="text"
                className="form-control"
                value={formData.first_name}
                onChange={(e) => setFormData({...formData, first_name: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Last Name *</label>
              <input
                type="text"
                className="form-control"
                value={formData.last_name}
                onChange={(e) => setFormData({...formData, last_name: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Email *</label>
              <input
                type="email"
                className="form-control"
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Address</label>
              <input
                type="text"
                className="form-control"
                value={formData.address}
                onChange={(e) => setFormData({...formData, address: e.target.value})}
              />
            </div>
            <div className="form-group">
              <label>City</label>
              <input
                type="text"
                className="form-control"
                value={formData.city}
                onChange={(e) => setFormData({...formData, city: e.target.value})}
              />
            </div>
            <div className="form-group">
              <label>Country</label>
              <input
                type="text"
                className="form-control"
                value={formData.country}
                onChange={(e) => setFormData({...formData, country: e.target.value})}
              />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input
                type="tel"
                className="form-control"
                value={formData.phone}
                onChange={(e) => setFormData({...formData, phone: e.target.value})}
              />
            </div>
            <button type="submit" className="btn btn-success">Add Customer</button>
          </form>
        </div>
      )}

      {error && <div className="error">Error: {error}</div>}
      {loading && <div className="loading">Loading...</div>}

      {/* Customer List */}
      {!loading && customers.length === 0 && (
        <p>No customers found.</p>
      )}

      {!loading && customers.length > 0 && (
        <div className="grid grid-2">
          {customers.map(customer => (
            <div key={customer.customer_id} className="card">
              <h3>{customer.first_name} {customer.last_name}</h3>
              <p><strong>ID:</strong> {customer.customer_id}</p>
              <p><strong>Email:</strong> {customer.email}</p>
              <div style={{ marginTop: '1rem', display: 'flex', gap: '0.5rem', flexWrap: 'wrap' }}>
                <Link to={`/customers/${customer.customer_id}`} className="btn btn-primary">
                  View Details
                </Link>
                <button 
                  className="btn btn-danger"
                  onClick={() => handleDelete(customer.customer_id)}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Pagination */}
      {!isSearching && totalPages > 1 && (
        <div className="pagination">
          <button
            onClick={() => setCurrentPage(p => Math.max(1, p - 1))}
            disabled={currentPage === 1}
            className="btn"
          >
            Previous
          </button>
          <span>Page {currentPage} of {totalPages}</span>
          <button
            onClick={() => setCurrentPage(p => Math.min(totalPages, p + 1))}
            disabled={currentPage === totalPages}
            className="btn"
          >
            Next
          </button>
        </div>
      )}
    </div>
  );
}

export default CustomerPage;
